// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import "../common/Authority.sol";
import "../interfaces/IHook.sol";
import "../libraries/LinkedAddressList.sol";

abstract contract ExecutionManager is Authority {
    using LinkedAddressList for mapping(address => address);

    error WrongArrayLength();
    error InvalidHook();

    event InstalledHook(address indexed hook);
    event UninstalledHook(address indexed hook);

    mapping(address => address) internal beforeHooks;
    mapping(address => address) internal afterHooks;

    function addHook(address hook, bytes calldata data) external onlySelf {
        IHook _hook = IHook(hook);
        if (!IHook(hook).supportsInterface(type(IHook).interfaceId)) {
            revert InvalidHook();
        }

        if (_hook.hookType() == HookType.Pre) {
            beforeHooks.add(hook);
        } else if (_hook.hookType() == HookType.Post) {
            afterHooks.add(hook);
        } else {
            beforeHooks.add(hook);
            afterHooks.add(hook);
        }
        _hook.install(data);

        emit InstalledHook(hook);
    }

    function removeHook(address prevBeforeHook, address prevAfterHook, address hook) external onlySelf {
        IHook _hook = IHook(hook);

        if (_hook.hookType() == HookType.Pre) {
            beforeHooks.remove(prevBeforeHook, hook);
        } else if (_hook.hookType() == HookType.Post) {
            afterHooks.remove(prevAfterHook, hook);
        } else {
            beforeHooks.remove(prevBeforeHook, hook);
            afterHooks.remove(prevAfterHook, hook);
        }
        _hook.uninstall();

        emit UninstalledHook(hook);
    }

    function sudo(address dest, uint256 value, bytes calldata func) external onlyEntryPoint {
        _call(dest, value, func);
    }

    function execute(address dest, uint256 value, bytes calldata func) external onlyEntryPoint {
        _execute(dest, value, func);
    }

    function executeBatch(address[] calldata dest, uint256[] calldata value, bytes[] calldata func)
        external
        onlyEntryPoint
    {
        if (dest.length != func.length || (value.length != 0 && value.length != func.length)) {
            revert WrongArrayLength();
        }
        if (value.length == 0) {
            for (uint256 i = 0; i < dest.length;) {
                _execute(dest[i], 0, func[i]);
                unchecked {
                    i++;
                }
            }
        } else {
            for (uint256 i = 0; i < dest.length;) {
                _execute(dest[i], value[i], func[i]);
                unchecked {
                    i++;
                }
            }
        }
    }

    function _execute(address target, uint256 value, bytes memory data) internal {
        address addr = beforeHooks[LinkedAddressList.SENTINEL];
        while (uint160(addr) > LinkedAddressList.SENTINEL_UINT) {
            IHook(addr).afterTransaction(target, value, data);
            addr = beforeHooks[addr];
        }

        _call(target, value, data);

        addr = afterHooks[LinkedAddressList.SENTINEL];
        while (uint160(addr) > LinkedAddressList.SENTINEL_UINT) {
            IHook(addr).afterTransaction(target, value, data);
            addr = afterHooks[addr];
        }
    }

    function _call(address target, uint256 value, bytes memory data) internal {
        (bool success, bytes memory result) = target.call{value: value}(data);
        if (!success) {
            assembly {
                revert(add(result, 32), mload(result))
            }
        }
    }
}

contracts/core/FallbackManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import "../common/Authority.sol";

abstract contract FallbackManager is Authority {
    error AddressCannotBeZero();

    // keccak-256 hash of "fallback_manager.handler.address" subtracted by 1
    bytes32 internal constant FALLBACK_HANDLER_STORAGE_SLOT =
        0x6c9a6c4a39284e37ed1cf53d337577d14212a4870fb976a4366c693b939918d4;

    event ChangedFallbackHandler(address indexed previousHandler, address indexed handler);

    fallback() external {
        bytes32 slot = FALLBACK_HANDLER_STORAGE_SLOT;
        assembly {
            let handler := sload(slot)
            if iszero(handler) { return(0, 0) }
            calldatacopy(0, 0, calldatasize())
            // The msg.sender address is shifted to the left by 12 bytes to remove the padding
            // Then the address without padding is stored right after the calldata
            mstore(calldatasize(), shl(96, caller()))
            // Add 20 bytes for the address appended add the end
            let success := call(gas(), handler, 0, 0, add(calldatasize(), 20), 0, 0)
            returndatacopy(0, 0, returndatasize())
            if iszero(success) { revert(0, returndatasize()) }
            return(0, returndatasize())
        }
    }

    function getFallbackHandler() external view returns (address _handler) {
        assembly {
            _handler := sload(FALLBACK_HANDLER_STORAGE_SLOT)
        }
    }

    function setFallbackHandler(address handler) public onlySelf {
        address previousHandler;
        assembly {
            previousHandler := sload(FALLBACK_HANDLER_STORAGE_SLOT)
        }
        _setFallbackHandler(handler);
        emit ChangedFallbackHandler(previousHandler, handler);
    }

    function _setFallbackHandler(address handler) internal {
        if (handler == address(0)) revert AddressCannotBeZero();
        bytes32 slot = FALLBACK_HANDLER_STORAGE_SLOT;
        assembly {
            sstore(slot, handler)
        }
    }
}

contracts/core/RecoveryManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import "../common/Authority.sol";
import "../common/Contants.sol";
import "../libraries/LinkedAddressList.sol";
import "../interfaces/IRecoveror.sol";

abstract contract RecoveryManager is Authority {
    error ErrorRecoveror(address);

    using LinkedAddressList for mapping(address => address);

    event AddedRecoveror(address recoveror);
    event RemovedRecoveror(address recoveror);

    mapping(address => address) internal recoverors;

    function getRecoverorsPaginated(address start, uint256 pageSize)
        external
        view
        returns (address[] memory array, address next)
    {
        return recoverors.page(start, pageSize);
    }

    function addRecoveror(address recoveror, bytes calldata data) external onlySelf {
        recoverors.add(recoveror);
        IRecoveror(recoveror).bind(data);
        emit AddedRecoveror(recoveror);
    }

    function removeRecoveror(address prevRecoveror, address recoveror) external onlySelf {
        recoverors.remove(prevRecoveror, recoveror);
        emit RemovedRecoveror(recoveror);
    }

    function isRecoverorEnabled(address recoveror) public view returns (bool) {
        return recoverors.contains(recoveror);
    }

    function _setupRecoverors() internal {
        recoverors.setup();
    }
}

contracts/core/ValidatorManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import "../common/Authority.sol";
import "../common/Contants.sol";
import "../libraries/LinkedAddressList.sol";
import "../interfaces/IValidator.sol";

abstract contract ValidatorManager is Authority {
    error ErrorValidator(address);

    using LinkedAddressList for mapping(address => address);

    error ValidatorCannotBeZeroOrSentinel(address validator);
    error ValidatorAlreadyEnabled(address validator);
    error ValidatorAndPrevValidatorMismatch(address expectedValidator, address returnedValidator, address prevValidator);

    event EnabledValidator(address validator);
    event DisabledValidator(address validator);

    mapping(address => address) internal validators;

    function getValidatorsPaginated(address start, uint256 pageSize)
        external
        view
        returns (address[] memory array, address next)
    {
        return validators.page(start, pageSize);
    }

    function _enableValidator(address validator, bytes calldata data) internal {
        validators.add(validator);
        IValidator(validator).enable(data);
        emit EnabledValidator(validator);
    }

    function enableValidator(address validator, bytes calldata data) external onlySelf {
        _enableValidator(validator, data);
    }

    function disableValidator(address prevValidator, address validator) external onlySelf {
        validators.remove(prevValidator, validator);
        emit DisabledValidator(validator);
    }

    function isValidatorEnabled(address validator) public view returns (bool) {
        return validators.contains(validator);
    }

    function _setupValidators() internal {
        validators.setup();
    }
}

